var slaveId = msg.modbusRequest.unitid;
var envId = env.get("Slave_Id");
var message = new Array(node.outputCount);

var batteryCapacity = msg.payload[0];
var batteryVoltage = msg.payload[1];
var chargingCurrent = msg.payload[2];
var temperature = msg.payload[3];
var loadDcVoltage = msg.payload[4];
var loadDcCurrent = msg.payload[5];
var loadDcPower = msg.payload[6];
var pvVoltage = msg.payload[7];
var pvCurrent = msg.payload[8];
var pvPower = msg.payload[9];

for (let index = 0; index < node.outputCount; index++) {
    message[index] = null;
}

if (slaveId == envId)
{
    message[0] = {payload : batteryCapacity};
    message[1] = {payload : batteryVoltage / 10};
    message[2] = {payload : chargingCurrent};
    message[3] = {payload : temperature};
    message[4] = {payload : loadDcVoltage / 10};
    message[5] = {payload : loadDcCurrent};
    message[6] = {payload : loadDcPower};
    message[7] = {payload : pvVoltage / 10};
    message[8] = {payload : pvCurrent};
    message[9] = {payload : pvPower};
}

return message;

